sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
      return;
   }

	function setOptions(options) {
		for(var value in options) {
			g_form.addOption('u_what_needed', value, options[value]);
		}

	}

   var whatNeeded = g_form.getValue('u_what_needed');

	// Clear all choices
	g_form.clearOptions('u_what_needed');

	// Set choices based on what was selected
	switch(newValue) {
		case 'hr':
			setOptions({
				hr1: 'Human Resources 1',
				hr2: 'Human Resources 2',
				other: 'Other'
			});
			break;

		case 'facilities':
			setOptions({
				facilities1: 'Facilities 1',
				facilities2: 'Facilities 2',
				other: 'Other'
			});
			break;

		case 'legal':
			setOptions({
				legal1: 'Legal 1',
				legal2: 'Legal 2',
				other: 'Other'
			});
			break;

		case 'it':
			setOptions({
				it1: 'IT 1',
				it2: 'IT 2',
				other: 'Other'
			});
	}

	if(isLoading && !g_form.isNewRecord()) {
		g_form.setValue('u_what_needed', whatNeeded);
	}

}
